mapper.select:
 SELECT STU_NO, NAME, KOR, ENG, MATH, AVE
   FROM STUDENT_T
   WHERE STU_NO = #{stuNo}

mapper.insert:
INSERT INTO STUDENT_T (
    STU_NO
  , NAME
  , KOR
  , ENG
  , MATH
  , AVE
  
) VALUES (
    STUDENT_SEQ.NEXTVAL
  , #{name}
  , #{kor}
  , #{eng}
  , #{math}
  , #{ave}
)

mapper.update:
UPDATE STUDENT_T
SET NAME = #{name}
  , KOR = #{kor}
  , ENG = #{eng}
  , MATH = #{math}
WHERE STU_NO = #{stu_no}
