mapper.select:
SELECT STU_NO, NAME, KOR, ENG, MATH, AVE, GRADE
  FROM STUDENT_T
 WHERE AVE BETWEEN #{begin} AND #{end}

SELECT COUNT(*)
  FROM STUDENT_T
 WHERE AVE BETWEEN #{begin} AND #{end}

SELECT NVL(AVG(AVE), 0)
  FROM STUDENT_T
 WHERE AVE BETWEEN #{begin} AND #{end}

SELECT STU_NO, NAME, KOR, ENG, MATH, AVE, GRADE
  FROM STUDENT_T
 WHERE STU_NO = #{stuNo}

mapper.update:
UPDATE STUDENT_T
   SET NAME = #{name}
     , KOR = #{kor}
     , ENG = #{eng}
     , MATH = #{math}
     , AVE = (#{kor} + #{eng} + #{math}) / 3
     , GRADE = 
        (CASE
           WHEN (#{kor} + #{eng} + #{math}) / 3 >= 90 THEN 'A'
           WHEN (#{kor} + #{eng} + #{math}) / 3 >= 80 THEN 'B'
           WHEN (#{kor} + #{eng} + #{math}) / 3 >= 70 THEN 'C'
           WHEN (#{kor} + #{eng} + #{math}) / 3 >= 60 THEN 'D'
           ELSE 'F'
         END)
 WHERE STU_NO = #{stuNo}
